import MtG from "../../../services/mtg"
import "./results-grid.scss"

@customElement(`results-grid`)
@containerless
@inject(MtG)
export class ResultsGrid {
  @bindable cards
  cards = []
  page = 1
  constructor(mtg) {
    this.log = LogManager.getLogger(`Compendium/${this.constructor.name}`)
    this.mtg = mtg
  }

  async loadMore() {
    this.page++
    // TODO: Config should be generated by a search component.
    let config = {
      set: [`ogw`],
      pageSize: 24,
      page: this.page
    }
    this.cards = await this.mtg.getCards(config)
  }
}
